"use client"

import React, { useState } from 'react';
import { 
  Card, 
  CardContent, 
  CardHeader, 
  CardTitle 
} from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { 
  MapPin,
  Users,
  Clock,
  Wrench,
  Camera,
  Lightbulb,
  Mic,
  Palette,
  ChevronDown,
  ChevronRight,
  FileText,
  BarChart3,
  Home,
  TreePine,
  Star,
  AlertCircle,
  Settings,
  CheckCircle,
  AlertTriangle
} from 'lucide-react';
import { SceneBreakdownOutput } from './types';

interface SceneBreakdownProps {
  data: SceneBreakdownOutput | string | any; // Accept raw string, parsed object, or any structure
}

export function SceneBreakdown({ data }: SceneBreakdownProps) {
  // Parse raw data if it's a string
  const parsedData = React.useMemo(() => {
    if (!data) return null;

    // If it's a string, clean and parse it
    if (typeof data === 'string') {
      try {
        // Clean the string data - remove markdown code blocks and extra formatting
        let cleanedData = data
          // Remove markdown code block syntax
          .replace(/^```json\s*/i, '')
          .replace(/^```\s*/i, '')
          .replace(/\s*```\s*$/i, '')
          // Remove leading/trailing whitespace
          .trim()
          // Remove any BOM or invisible characters at start
          .replace(/^\uFEFF/, '')
          // Fix common JSON issues
          .replace(/,(\s*[}\]])/g, '$1') // Remove trailing commas
          .replace(/([{,]\s*)(\w+):/g, '$1"$2":'); // Quote unquoted keys

        const parsed = JSON.parse(cleanedData);
        // Check for progressiveSceneBreakdownOutput structure
        if (parsed.progressiveSceneBreakdownOutput) {
          return parsed.progressiveSceneBreakdownOutput;
        }
        return parsed as SceneBreakdownOutput;
      } catch (error) {
        console.error('Failed to parse scene breakdown raw data after cleaning:', error);
        console.error('Original data preview:', data.substring(0, 200));
        return null;
      }
    }

    // If it's an object, handle different structures
    if (typeof data === 'object') {
      // Check for new progressiveSceneBreakdownOutput structure
      if (data.progressiveSceneBreakdownOutput) {
        return data.progressiveSceneBreakdownOutput;
      }
      // Check for old structure
      if (data.phase_1_screenplay_breakdown) {
        return data as SceneBreakdownOutput;
      }
      // Check if this is actually EighthsAnalysis data (has sceneAnalysisSummary)
      if (data.sceneAnalysisSummary && data.projectId) {
        console.warn('SceneBreakdown received EighthsAnalysis data - this data belongs to EighthsAnalysis component');
        return null;
      }

      // Look for nested scene breakdown data - try various patterns
      if (data.sceneBreakdownOutput) return data.sceneBreakdownOutput;
      if (data.sceneBreakdownAgent) return data.sceneBreakdownAgent;
      if (data.parsed) return data.parsed;
      if (data.raw) {
        try {
          const rawParsed = typeof data.raw === 'string' ? JSON.parse(data.raw) : data.raw;
          if (rawParsed.progressiveSceneBreakdownOutput) {
            return rawParsed.progressiveSceneBreakdownOutput;
          }
          return rawParsed;

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center justify-center rounded-md border px-2 py-0.5 text-xs font-medium w-fit whitespace-nowrap shrink-0 [&>svg]:size-3 gap-1 [&>svg]:pointer-events-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive transition-[color,box-shadow] overflow-hidden",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground [a&]:hover:bg-primary/90",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground [a&]:hover:bg-secondary/90",
        destructive:
          "border-transparent bg-destructive text-white [a&]:hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "text-foreground [a&]:hover:bg-accent [a&]:hover:text-accent-foreground"
      }
    },
    defaultVariants: {
      variant: "default"
    }
  }
)

function Badge({
  className,
  variant,
  asChild = false,
  ...props
}: React.ComponentProps<"span"> &
  VariantProps<typeof badgeVariants> & { asChild?: boolean }) {
  const Comp = asChild ? Slot : "span"

  return (
    <Comp
      data-slot="badge"
      className={cn(badgeVariants({ variant }), className)}
      {...props}
    />
  )
}

export { Badge, badgeVariants }

